import './App.css';
import React, { Component } from 'react'

class App extends Component {

  url = 'http://localhost:8080/'
  // name = ''
  state = {
    users: [],
    selectedId: 0 
  }


  addUser() {
    fetch(this.url + 'user/alex', {method: 'POST'})
    .then(res => res.json())
    .then((data) => {
      this.setState({users: [...this.state.users, data], selectedId: this.state.selectedId})
      this.render()
    })
    .catch(console.log)
  }

  getUser(i) {
    console.log('going to get user ', i)
    let userName = this.state.users[i].name
    fetch(this.url + `user/${userName}`)
    .then(res => res.json())
    .then((data) => {
      this.setState({users: this.state.users, selectedId: data})
      this.render()
    })
    .catch(console.log)
  }

  render() {
    return (
      <div class="app">
        <button onClick={() => this.addUser()}>
          Add user
        </button>
        {this.state?.users.map((user, i) => {
          return <div 
            onClick={() => this.getUser(i)}
            key={i}> 
            {user.name} 
          </div>
        })} 
      </div>
    )
  }
}

export default App;
