import './App.css';
import React, { Component } from 'react'

class App extends Component {

  url = 'http://localhost:8080/'
  state = {
    users: [],
    selectedId: 0,
    selectedIndex: 0,
    nameInput: ''
  }


  addUser() {
    fetch(this.url + 'user/alex', {method: 'POST'})
    .then(res => res.json())
    .then((data) => {
      this.setState({users: [...this.state.users, data]})
      this.render()
    })
    .catch(console.log)
  }

  userClicked(i) {
    this.setState({selectedIndex: i})
    this.getUser(i)
    console.log(this.state.selectedIndex)
  }

  getUser(i) {
    console.log('going to get user ', i)
    let id = this.state.users[i].id
    fetch(this.url + `user/${id}`)
    .then(res => res.json())
    .then((data) => {
      this.setState({selectedId: data})
      this.render()
    })
    .catch(console.log)
  }

  nameChanged(event) {
    const name = event.target.value
    this.setState({nameInput: name})
  }

  render() {
    return (
      <div class="app">
        <form>
          <p>Enter your name:</p>
          <input
            type="text"
            onChange={(event) => this.nameChanged(event)}
          />
        </form>
        <button onClick={() => this.addUser()}>
          Add user
        </button>
        {this.state?.users.map((user, i) => {
          return <div 
            style={{color: this.state.selectedIndex === i ? "yellow" : "white"}}
            onClick={() => this.userClicked(i)}
            key={i}> 
            { this.state.selectedIndex === i ? user.name : user.id} 
          </div>
        })} 
      </div>
    )
  }
}

export default App;
